import React from 'react';
import FormOptimizacion from './FormOptimizacion';
import '../css/optimizacion.css';
import 'bootstrap/dist/css/bootstrap.css';
import Resultados from './Resultados';

class OptimizacionLineal extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            resultados: false,
            url: '',
        }
    }

    mostrarResultados = () =>{
        this.setState(() => ({
            url: window.location.href,
            resultados: true,
        }))
    }

    render(){
        return(
            <div>
                {!this.state.resultados && <FormOptimizacion cambiarEstado = {this.mostrarResultados}/>}
                {this.state.resultados && <Resultados urlDatos = {this.state.url}/>}
            </div>
        )
    }
}

export default OptimizacionLineal;
