import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import '../css/optimizacion.css';
import SeccionDato from './SeccionDato';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { plus } from '@fortawesome/free-solid-svg-icons'

class SeccionForm extends React.Component{
    render(){
        return(
            <div className="seccionForm">
                <div class="encabezado">
            <h2><i class="fas fa-ice-cream"></i>{this.props.titulo}</h2>
            <div class="form-group row">
              <div class="col-9 text">
                <h4>{this.props.nombreTipo}</h4>
              </div>
              <div class="col-3">
                <h4><i class="fas fa-chart-line"></i>{this.props.nombreCantidad}</h4>
              </div>
            </div>
          </div>
                <SeccionDato nombre={this.props.nombreTipo} />
                <SeccionDato nombre={this.props.nombreTipo} />
                <SeccionDato nombre={this.props.nombreTipo} />
                <button type="button" className="btn btn-dark" id="agregarSabor"><FontAwesomeIcon icon={faCoffee} /> Agregar {this.props.nombreTipo}</button>
            </div>
        )
    }
}

export default SeccionForm;
