import React from 'react';
import InputCamion from './InputCamion';

class SeccionCamiones extends React.Component{

    constructor(props){
        super(props);
        this.state = {
            inputCamiones:[1],
        }

    }

    AgregarCamion = () =>{
        console.log("hola")
        let nuevoInput = this.state.inputCamiones.length + 1;
        this.setState( prevState => ({camiones: prevState.inputCamiones.concat([nuevoInput])}))
      }

    render(){
        return(
            <div>
                <h1>Camiones</h1>
                <p>Especificar cantidad de camiones y capacidad de cada uno</p>
                <div className="form-inline">
                    {this.state.inputCamiones.map(input => <InputCamion key = {input} nro = {input}/> )}
                    <button type="button" className="btn btn-secondary" id="agregarSabor" onClick={this.AgregarCamion}> Agregar cami√≥n</button>
                </div>
            </div>
                
        )
    }
}

export default SeccionCamiones;
