import React, { Component } from 'react';
import {useLocation} from 'react-router-dom';

function useQuery() {
    return new URLSearchParams(useLocation().search);
}

function Resultados(){
    let query = useQuery();
    console.log(query);
    let i = 0;
    while(i<10){
        console.log(i);
    }
    return(
        <div>
            <h1>Se muestran los resultados para la url</h1>
            <p>{query}</p>
        </div>
    )
}

export default Resultados;
