import React from 'react';
import FormOptimizacion from './FormOptimizacion';
import '../css/optimizacion.css';
import 'bootstrap/dist/css/bootstrap.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCode } from '@fortawesome/free-solid-svg-icons'

class OptimizacionLineal extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            resultados: false,
            url: '',
        }
    }

    mostrarResultados = () =>{
        this.state.url = window.location.href;
        this.state.resultados = true;
        alert('chota');
    }

    render(){
        return(
            <div>
                {!this.state.resultados && <FormOptimizacion onSumbit = {this.mostrarResultados}/>}
                <h1>{this.state.url}</h1>
            </div>
        )
    }
}

export default OptimizacionLineal;
