import React from 'react';
import axios from "axios";
import { Chart } from "react-google-charts";

axios.defaults.xsrfCookieName = 'csrftoken';
axios.defaults.xsrfHeaderName = 'X-CSRFToken';

class ResultadosScheduling extends React.Component{

    constructor(props){
        super(props)
        this.state = {
            datos: [],
        }
    }

    usarQuery() {
        const search = window.location.href; // could be '?foo=bar'
        let params =  new URLSearchParams(search)
        return params;
    }

    requestResults = (hel) => {
        axios
        .post("http://localhost:8000/scheduling/", {helados: hel})
        .then(res => {
            console.log(res);
            this.setState({ datos: res.data })})
        .catch(err => {console.log(err)});
    }

    componentDidMount(){
        let query = this.usarQuery();
        let parametros = Array.from(query.entries());
        let id_helados = [];
        console.log(" parametros es ",parametros)
        if(parametros != null){
            parametros[0][0] = "sabor1"; //porque devuelve como primero toda la url
            for (let i=0; i<parametros.length; i++){
                console.log(parametros[i])
                id_helados.push(parametros[i][1])
            }
            console.log("id helados es",id_helados);
            this.requestResults(id_helados);
        }

    }

    render(){
        let sumaHoras = (horas) =>{
            var dt = new Date();
            console.log(dt);
            dt.setHours( dt.getHours() + horas );
            console.log(dt);
            return dt;
        }
    
        let corrige_horas = (rows) =>{
            for(let i =1; i < rows.length; i++){
                rows[i][3] = sumaHoras(rows[i][3]);
                rows[i][4] = sumaHoras(rows[i][4]);
                rows[i][5] = null;
            }
            return rows
        }



        const rows= [
            [
                { type: 'string', label: 'Task ID' },
                { type: 'string', label: 'Task Name' },
                { type: 'string', label: 'Resource' },
                { type: 'date', label: 'Start Date' },
                { type: 'date', label: 'End Date' },
                { type: 'number', label: 'Duration' },
                { type: 'number', label: 'Percent Complete' },
                { type: 'string', label: 'Dependencies' },
            ],
        ['Banana SplitMezcladora', 'Banana Split en Mezcladora', 'Mezcladora', 0, 2, 2, 100, null],
        ['Dulce de lecheMezcladora', 'Dulce de leche en Mezcladora', 'Mezcladora', 2, 4, 2, 100, null], 
        ['FrambuesaMezcladora', 'Frambuesa en Mezcladora', 'Mezcladora', 4, 5, 1, 100, null], 
        ['Banana SplitPasteurizadora', 'Banana Split en Pasteurizadora', 'Pasteurizadora', 2, 5, 3, 100, 'Banana SplitMezcladora'], 
        ['Dulce de lechePasteurizadora', 'Dulce de leche en Pasteurizadora', 'Pasteurizadora', 5, 8, 3, 100, 'Dulce de lecheMezcladora'], 
        ['FrambuesaPasteurizadora', 'Frambuesa en Pasteurizadora', 'Pasteurizadora', 8, 10, 2, 100, 'FrambuesaMezcladora'], 
        ['Banana SplitMaduradora', 'Banana Split en Maduradora', 'Maduradora', 5, 11, 6, 100, 'Banana SplitPasteurizadora'], 
        ['Dulce de lecheMaduradora', 'Dulce de leche en Maduradora', 'Maduradora', 11, 19, 8, 100, 'Dulce de lechePasteurizadora'], 
        ['FrambuesaMaduradora', 'Frambuesa en Maduradora', 'Maduradora', 19, 27, 8, 100, 'FrambuesaPasteurizadora'], 
        ['Banana SplitMantecadora', 'Banana Split en Mantecadora', 'Mantecadora', 11, 13, 2, 100, 'Banana SplitMaduradora'], 
        ['Dulce de lecheMantecadora', 'Dulce de leche en Mantecadora', 'Mantecadora', 19, 20, 1, 100, 'Dulce de lecheMaduradora'], 
        ['FrambuesaMantecadora', 'Frambuesa en Mantecadora', 'Mantecadora', 27, 28, 1, 100, 'FrambuesaMaduradora'], 
        ['Banana SplitEnvasadora', 'Banana Split en Envasadora', 'Envasadora', 13, 14, 1, 100, 'Banana SplitMantecadora'], 
        ['Dulce de lecheEnvasadora', 'Dulce de leche en Envasadora', 'Envasadora', 20, 22, 2, 100, 'Dulce de lecheMantecadora'], 
        ['FrambuesaEnvasadora', 'Frambuesa en Envasadora', 'Envasadora', 28, 30, 2, 100, 'FrambuesaMantecadora'], 
        ['Banana SplitBatidora', 'Banana Split en Batidora', 'Batidora', 14, 16, 2, 100, 'Banana SplitEnvasadora'], 
        ['Dulce de lecheBatidora', 'Dulce de leche en Batidora', 'Batidora', 22, 23, 1, 100, 'Dulce de lecheEnvasadora'], 
        ['FrambuesaBatidora', 'Frambuesa en Batidora', 'Batidora', 30, 32, 2, 100, 'FrambuesaEnvasadora'], 
        ['Banana SplitConservadora', 'Banana Split en Conservadora', 'Conservadora', 16, 24, 8, 100, 'Banana SplitBatidora'], 
        ['Dulce de lecheConservadora', 'Dulce de leche en Conservadora', 'Conservadora', 24, 36, 12, 100, 'Dulce de lecheBatidora'], 
        ['FrambuesaConservadora', 'Frambuesa en Conservadora', 'Conservadora', 36, 46, 10, 100, 'FrambuesaBatidora']];
        
        let nuevo_rows = corrige_horas(rows);
        console.log(nuevo_rows)


        return(
            <div>
                <h1>Resultados scheduling</h1>
                <h5>La duración óptima es de {this.state.datos.duracion_optima} horas.</h5>
                <p>{this.state.datos.toString()}</p>
                <div style={{ display: 'flex', maxWidth: 900 }}>
                <Chart
  width={'100%'}
  height={'1200px'}
  chartType="Gantt"
  loader={<div>Loading Chart</div>}
  data={nuevo_rows}

  

  rootProps={{ 'data-testid': '3' }}
/>
</div>
            </div>
        )
    }
}

export default ResultadosScheduling;
