import React from 'react';
import FormOptimizacion from './FormOptimizacion';
import '../css/optimizacion.css';
import 'bootstrap/dist/css/bootstrap.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCode } from '@fortawesome/free-solid-svg-icons'

class OptimizacionLineal extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            resultados: false,
            url: '',
        }
    }

    mostrarResultados = (url) =>{
        this.setState(() => ({
            url: window.location.href,
            resultados: true,
        }))
        this.state.url = url;
        this.state.resultados = true;
        alert('estado url esurl es' + this.state.url);
    }

    render(){
        return(
            <div>
                <h1>url es {this.state.url}</h1>
                {!this.state.resultados && <FormOptimizacion cambiarEstado = {this.mostrarResultados}/>}
            </div>
        )
    }
}

export default OptimizacionLineal;
